mapper.update:
UPDATE clubs
club_name = #{clubName:VARCHAR}, club_intro = #{clubIntro:VARCHAR}, club_max_member_no = #{clubMaxMemberNo:INTEGER}, club_img = #{clubImg:VARCHAR}, club_region = #{clubRegion:VARCHAR}, filter_gender = #{filterGender:INTEGER}, filter_min_age = #{filterMinAge:INTEGER}, filter_max_age = #{filterMaxAge:INTEGER}, filter_tag = #{filterTag:VARCHAR}
WHERE club_no = #{clubNo:INTEGER}
