mapper.update:
UPDATE meetings
SET entry_fee = #{entryFee}, meeting_date = #{meetingDate}, meeting_start_time = #{meetingStartTime}, meeting_end_time = #{meetingEndTime}, parent_club_no = #{parentClubNo}, meeting_addr = #{meetingAddr}, meeting_detail_addr = #{meetingDetailAddr}, meeting_lat = #{meetingLat}, meeting_lng = #{meetingLng}, meeting_name = #{meetingName}, meeting_intro = #{meetingIntro}, meeting_leader_no = #{meetingLeaderNo}, meeting_max_member_no = #{meetingMaxMemberNo}, meeting_reg_date = #{meetingRegDate}, meeting_state = #{meetingState}, meeting_img = #{meetingImg}, filter_gender = #{filterGender}, filter_min_age = #{filterMinAge}, filter_max_age = #{filterMaxAge}, filter_tag = #{filterTag}, adjustment_time = #{adjustmentTime}, adjustment_state = #{adjustmentState}, account_no = #{accountNo}, bank_name = #{bankName}
WHERE meeting_no = #{meetingNo}

UPDATE meetings
SET meeting_success = #{meetingSuccess}, meeting_state = #{meetingState}, adjustment_time = #{adjustmentTime}, adjustment_state = #{adjustmentState}, account_no = #{accountNo}, bank_name = #{bankName}
WHERE meeting_no = #{meetingNo}

UPDATE members
SET state = #{state}
WHERE member_no = #{memberNo}

mapper.select:
SELECT meeting_review_no, meeting_no, meeting_score, meeting_review_img, meeting_reviewer_no
FROM meeting_reviews
WHERE meeting_no = #{meetingNo}

mapper.update:
UPDATE meeting_reviews
SET meeting_score = #{meetingScore}, meeting_review_img = #{meetingReviewImg}, meeting_review_content = #{meetingReviewContent}
WHERE meeting_review_no = #{meetingReviewNo}
